mapper.insert:
insert into teacher (teacher_id, teacher_name, teacher_sex, teacher_age, teacher_email,
                     teacher_phone, teacher_nation, major_id, teacher_position)
values (#{teacher_id},#{teacher_name},#{teacher_sex},#{teacher_age},#{teacher_email},#{teacher_phone},#{teacher_nation},#{major_id},#{teacher_position});

mapper.select:
SELECT teacher_id,teacher_name,teacher_sex,teacher_age,teacher_email,teacher_phone,teacher_nation,
  teacher_name,teacher_position,major_name
FROM teacher,major WHERE teacher.major_id = major.major_id AND teacher_id=#{teacher_id}
